import React, { useState } from "react";
import {
  GoogleMap,
  Marker,
  withGoogleMap,
  withScriptjs,
} from "react-google-maps";

const MAP_URL = `https://maps.googleapis.com/maps/api/js?key=AIzaSyCSA62-dhozQFO_pcHIqDVRIQ_bv7tiXdQ&v=3.exp&libraries=geometry`;

const MapComp = withScriptjs(
  withGoogleMap((props) => (
    <GoogleMap defaultZoom={5.8} defaultCenter={{ lat: 41.8719, lng: 12.5674 }}>
      {props.children}
    </GoogleMap>
  ))
);

const Map = (props) => {
  const [state, setState] = useState({
    isOpen: false,
  });

  handleToggleOpen = () => {
    this.setState({
      isOpen: true,
    });
  };

  handleToggleClose = () => {
    this.setState({
      isOpen: false,
    });
  };
  return (
    <MapComp
      googleMapURL={MAP_URL}
      loadingElement={<div style={{ height: `100%` }} />}
      containerElement={<div style={{ height: `100vh` }} />}
      mapElement={<div style={{ height: `100%` }} />}
      center={{ lat: 41.8719, lng: 12.5674 }}
    >
      {props.cordinates.map((item, index) => (
        <Marker
          key={index}
          position={{ lat: parseFloat(item.lat), lng: parseFloat(item.long) }}
          onClick={() => this.handleToggleOpen()}
        >
          state.isOpen &&
          <InfoWindow onCloseClick={this.handleToggleClose}>
            {JSON.stringify(item)}
          </InfoWindow>
        </Marker>
      ))}
    </MapComp>
  );
};

export default Map;
