import axios from "axios";
import React, { useEffect, useState } from "react";
import LeftSideBar from "../components/LeftSideBar";
import Map from "../components/Map";
import RightSideBar from "../components/RightSideBar";
import Layout from "../Layout";

const Index = () => {
  const [state, setState] = useState([]);

  useEffect(() => {
    getData("province");
  }, []);

  const getData = (route) => {
    axios.get(process.env.API_URL + route).then((res) => {
      let data = res.data;

      data = data.filter(
        (item) =>
          item.denominazione_provincia != "In fase di definizione/aggiornamento"
      );

      setState(data);
    });
  };

  return (
    <Layout>
      <main className="container-fluid position-absolute h-100 bg-light">
        <div className="row position-absolute w-100 h-100">
          <section className="col-md-2 position-relative d-flex flex-wrap h-100 align-items-start align-content-between bg-white px-0">
            <LeftSideBar />
          </section>

          <section className="col-md-8 px-0 border-right border-left border-gray position-relative d-flex flex-wrap h-100 align-items-start align-content-between bg-white px-0">
            <div
              className="d-flex flex-wrap align-items-start align-content-start position-relative"
              style={{ height: "calc(100% - 90px)" }}
            ></div>
            <Map cordinates={state} />
            <div
              className="border-bottom border-gray w-100 px-2 d-flex align-items-center bg-white justify-content-between"
              style={{ height: 90 }}
            >
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. ?
            </div>
          </section>

          <section
            className="col-md-2 position-relative d-flex flex-wrap h-100 align-items-start align-content-between bg-white px-1 py-1"
            style={{ overflowY: "scroll" }}
          >
            <RightSideBar getData={getData} data={state} />
          </section>
        </div>
      </main>
    </Layout>
  );
};

export default Index;
