import axios from "axios";
import React, { useEffect, useState } from "react";
import {
  GoogleMap,
  Marker,
  withGoogleMap,
  withScriptjs,
} from "react-google-maps";

const MAP_URL = `https://maps.googleapis.com/maps/api/js?key=AIzaSyCSA62-dhozQFO_pcHIqDVRIQ_bv7tiXdQ&v=3.exp&libraries=geometry`;

const MapComp = withScriptjs(
  withGoogleMap((props) => (
    <GoogleMap defaultZoom={8} defaultCenter={{ lat: -34.397, lng: 150.644 }}>
      {props.children}
    </GoogleMap>
  ))
);

const Map = () => {
  const [state, setState] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8000").then((res) => {
      res.lat = parseFloat(res.lat);
      res.long = parseFloat(res.long);
      setState(res.data);
    });
  }, []);

  return (
    <MapComp
      googleMapURL={MAP_URL}
      loadingElement={<div style={{ height: `100%` }} />}
      containerElement={<div style={{ height: `700px` }} />}
      mapElement={<div style={{ height: `100%` }} />}
      center={{ lat: 25.03, lng: 121.6 }}
    >
      {state.map((item, index) => (
        <Marker position={{ lat: item.lat, lng: item.long }} />
      ))}
    </MapComp>
  );
};

export default Map;
